function getTimeRemaining(e){const t=Date.parse(e)-Date.parse(new Date),n=Math.floor(t/1e3%60);return{total:t,minutes:Math.floor(t/1e3/60%60),seconds:n}}function initializeClock(e){const t=document.querySelector(".minutes"),n=document.querySelector(".seconds"),s=document.querySelector("body"),o=document.querySelector(".modal");function l(){let l=getTimeRemaining(e);if(t.innerHTML=("0"+l.minutes).slice(-2),n.innerHTML=("0"+l.seconds).slice(-2),l.total<=0)return s.classList.toggle("modal_open"),o.classList.toggle("modal_close"),clearInterval(c),!0}document.querySelector(".close").addEventListener("click",(function(){s.classList.toggle("modal_open"),o.classList.toggle("modal_close")})),l();const c=setInterval(l,1e3)}const deadline=new Date(Date.parse(new Date)+5e6);initializeClock(deadline);let tabs=document.querySelector(".menu__tabs"),btns=tabs.querySelectorAll(".tabs__btn"),items=tabs.querySelectorAll(".tabs__item");function change(e,t){e.forEach(e=>{e.forEach(e=>{e.classList.remove("is-active")}),e[t].classList.add("is-active")})}for(let e=0;e<btns.length;e++)btns[e].addEventListener("click",()=>{change([btns,items],e)});